    {
        _sandwich.Vegetables = "Lettuce, Tomato";
    }

    public void AddSauces()
    {
        _sandwich.Sauces = "Mayonnaise";
    }

    public Sandwich GetSandwich()
    {
        return _sandwich;
    }
}

//Clase concreta que implementa la interfaz ISandwichBuilder y que construye un sandwich tipo Club
public class ClubSandwichBuilder : ISandwichBuilder
{
