        Console.WriteLine("Club Sandwich: Bread - " + club.Bread + ", Meat - " + club.Meat + ", Cheese - " + club.Cheese + ", Vegetables - " + club.Vegetables + ", Sauces - " + club.Sauces);

        Console.ReadLine();
    }
}

