matrix3 MatrixFromNormal(point3 normal) {
  matrix3 result;
  float length = sqrtf(normal.x * normal.x + normal.y * normal.y + normal.z * normal.z);
  result.z = (point3){normal.x / length, normal.y / length, normal.z / length};
  if (fabsf(result.z.x) < fabsf(result.z.y)) {
    float a = 1.0f / sqrtf(result.z.y * result.z.y + result.z.z * result.z.z);
    result.x = (point3){result.z.z * a, 0.0f, -result.z.y * a};
